document.addEventListener("alpine:init",()=>{Alpine.magic("modal",()=>(e,t={},n=null)=>{const d={modalComponent:e,arguments:t};n&&(d.source=n),Livewire.dispatch("noerdModal",d)}),Alpine.store("app",{currentId:null,modalOpen:!1,setId(e){this.currentId=e}})});document.addEventListener("set-app-id",e=>{Alpine.store("app").setId(e.detail.id)});document.addEventListener("modal-closed-global",()=>{Alpine.store("app").modalOpen=!1});
